import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

export default class SalesOrders extends Component { 
    callAPI() {
        fetch("http://localhost:3001/products/new")
            .then(res => res.json())
            .then(res => this.setState({ data: res }));
    }

    componentDidMount() {
        this.callAPI();
    }

    newProductHandler = (data) =>{
        //set data to be sent to db in newProduct
        const newProduct = {
            name: data.name,
            price: data.price,
            qty: data.qty        
        }

        fetch("http://localhost:3001/products", {
            method: 'POST',
            body: JSON.stringify(newProduct), 
            headers: {"Content-Type": "application/json"}
        })
        .then((response) => {
            console.log(response)
            if(response.ok){
                return response.json()
            } else {
                throw Error(`Request rejected with status ${response.status}`);
            }
            })
        .then((response) => 
            console.log(response)
        )
        .catch((err) => console.log('error' + err))
    }

    submitHandler = (e) => {
        e.preventDefault();
        const newProduct = {
          name: e.target.elements['name'].value,
          price: e.target.elements['price'].value,
          qty: e.target.elements['qty'].value,
        }
        this.props.onSubmit(newProduct)
    }

    render() {         
        return (                                     
            <div className="sales-orders-container">
                <Form onSubmit={this.submitHandler}>
                    <Form.Group>
                        <Form.Label>
                            Product name
                        </Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Product name" 
                            name="name"
                        />
                    </Form.Group>

                    <Form.Group>
                        <Form.Label>
                            Price
                        </Form.Label>
                        <Form.Control 
                            type="money"   
                            placeholder="Price" 
                            name="price"
                        />
                    </Form.Group>

                    <Form.Group>
                        <Form.Label>
                            Qty
                        </Form.Label>
                        <Form.Control 
                            type="number" 
                            placeholder="Qty"
                            name="qty"
                        />
                    </Form.Group>
                    <Button 
                        variant="primary" 
                        type="submit"
                    >
                        Submit
                    </Button>
                </Form>
            </div>
        )
    }
}
